(()=>{const e=document.querySelector(".input"),t=document.querySelector(".button"),a=document.querySelector(".wrapper");e.addEventListener("input",(()=>{t.disabled=""===e.value,a.style.border="2px solid white"})),t.addEventListener("click",(t=>{if(t.preventDefault(),e.value){const t=`https://api.openweathermap.org/data/2.5/weather?lat=${e.value}&lon=50&appid=f4cac63c1c37398ab9151f7d93014510`;fetch(t,{method:"POST",body:e.value}).then((t=>{200===t.status?(alert("Сообщение отправлено!"),e.value=""):alert("Введите номер от 0 до 90!")})).catch((e=>alert("Error: ",e)))}else a.style.border="2px solid red",alert("Введите номер!")}))})();